import React, { Fragment, useState, useCallback } from 'react';
import {
    View, Text, StatusBar, SafeAreaView, Image, TouchableOpacity, TextInput,
    ImageBackground, Platform,
} from 'react-native';
import styles from './styles';
import Colors from '../../../utills/Colors';
import { Header } from '../../../components/Headers/index';
import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scrollview';
import { width, height, totalSize } from 'react-native-dimension';
import AntDesign from 'react-native-vector-icons/AntDesign';
import Ionicons from 'react-native-vector-icons/Ionicons';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import ImagePicker from 'react-native-image-crop-picker';
import { FlatList } from 'react-native-gesture-handler';
import { AddCommentTopicReplies, s3Upload } from '../../../utills/ApiFunctions';
import { debounce } from "lodash";
import { setIsLoader } from '../../../Redux/Actions/config';
import { updateComments } from '../../../Redux/Actions/Comments';
import { useDispatch, useSelector } from 'react-redux';
import { GetImagePath } from '../../../utills/Methods';
import { showMessage } from 'react-native-flash-message';
import { handleEmailError } from '../../../utills/GlobalFunction';
export default function DiscussionForumComment({ navigation, route }) {
    const size = `500x500`
    const profileImageSize = `50x50`
    const topicId = route?.params?.topicId;
    const isHome = route?.params?.isHome ?? false
    const user = useSelector((state) => state.Auth.user);
    const dispatch = useDispatch();
    const [newRepliesImage, setNewRepliesImage] = useState('')
    const [newReplies, setNewCommentReplies] = useState('')
    const [commentData, setCommentData] = useState(route?.params?.commentData)
    const [buttonDisable, setButtonDisable] = useState(false)
    const _showMessage = (description) => {
        showMessage({
            message: 'Error',
            description: description,
            type: 'danger',
            duration: 8000
        });
    }
    const _handleEmailError = (subject = '', message = '') => {
        handleEmailError({
            Subject: subject,
            Message: message
        })
    }
    const AddReplies = async (commentImage = '', newRpleyTemp = newReplies) => {
        dispatch(setIsLoader(true))
        let obj = {
            TopicId: topicId,
            CommentId: commentData?.Id,
            UserName: `${user?.idToken.payload?.given_name} ${user?.idToken.payload?.family_name}`,
            UserImageUrl: user?.idToken?.userData?.ProfileImage,
            snsARN: user?.idToken?.userData?.snsARN
        };
        if (newRepliesImage != '' && newRpleyTemp != '')
            obj = {
                ...obj,
                CommentImages: commentImage,
                Comment: newRpleyTemp
            }
        if (newRpleyTemp == '' && newRepliesImage != '')
            obj = {
                ...obj,
                CommentImages: commentImage
            }
        if (newRepliesImage == '' && newRpleyTemp != '') {
            obj = {
                ...obj,
                Comment: newRpleyTemp
            }
        }
        const response = await AddCommentTopicReplies({
            data: obj
        })
        if (response?.success) {
            !isHome && dispatch(updateComments(response.data))
            setCommentData(response.data)
            setNewCommentReplies('')
            setNewRepliesImage('')
            dispatch(setIsLoader(false))
        }
        else {
            dispatch(setIsLoader(false))
            _showMessage('An error occurred while adding the replies.  If the problem persists, please contact booklovershelp@gmail.com.')
            _handleEmailError('Add replies', JSON.stringify(response))
        }
    }
    const _uploadS3 = async (image) => {
        const response = await s3Upload({
            key: "RepliesImage.jpg",
            imageType: 'Replies',
            base64String: image
        });
        if (response?.success) {
            AddReplies(response.data)
        }
        else if (response?.status === 401) {
          _showMessage('Your session has expired, please log back in.');
          dispatch(logout());
        }
        else {
            _showMessage('An error occurred while uploading the image for this reply.  If the problem persists, please contact booklovershelp@gmail.com.')
            _handleEmailError('Book Lovers Error: Uploading S3 Reply Image', JSON.stringify(response))
        }
    }
    const uploadImage = () => {
        ImagePicker.openPicker({
            width: 300,
            height: 400,
            cropping: true,
            includeBase64: true
        }).then(image => {
            setNewRepliesImage('data:image/jpeg;base64,' + image.data)
        });
    }
    const _showReplies = ({ item }) => {
        return (
            <View style={styles.repliesContainer}>
                <View style={styles.repliesInfoContainer}>
                    <Image source={{ uri: item?.UserImageUrl ? GetImagePath(item?.UserImageUrl, profileImageSize) : 'data:image/png;base64,' }}
                        resizeMode='contain' style={styles.replierImage}
                    />
                    <View style={styles.replierNameContainer}>
                        <Text numberOfLines={1} style={styles.replierNameText}>{item?.UserName ?? 'Anonymous'}</Text>
                    </View>
                </View>
                <View style={styles.repliesTextContainer}>
                    {item?.Comment && <Text style={styles.repliesText}>{item?.Comment}</Text>}
                    {item?.CommentImages && <Image source={{ uri: GetImagePath(item?.CommentImages, size) }}
                        resizeMode='cover' style={styles.repliesImage} />}
                </View>
            </View>
        )
    }
    const searchDebounceHandler = useCallback(debounce(newRepliesImage == '' ? (val) => AddReplies(undefined, val) : () => _uploadS3(newRepliesImage), 1000), []);
    const _postReplies = () => {
        setButtonDisable(true)
        searchDebounceHandler(newReplies)
    }
    return (
        <Fragment>
            <StatusBar
                barStyle={'dark-content'}
                backgroundColor={Colors.white}
            />
            <SafeAreaView
                style={(styles.container, { backgroundColor: Colors.white })}
            />
            <Header title={'Replies'} containerStyles={styles.containerStyles} isBack
                onPress={() => navigation.goBack()}
            />
            <View style={styles.container}>
                <KeyboardAwareScrollView
                    showsVerticalScrollIndicator={false}
                    keyboardShouldPersistTaps='handled'
                    nestedScrollEnabled={true}
                    automaticallyAdjustContentInsets={false}
                    containerStyles={styles.scrollView}
                >
                    <View style={[styles.commentsFlatListContainer, newRepliesImage != '' ? { height: Platform.OS === 'ios' ? height(65) : height(72) } : {}]}>
                        <FlatList
                            data={commentData?.Replies}
                            style={{ flex: 1 }}
                            nestedScrollEnabled={true}
                            ItemSeparatorComponent={() => <View style={styles.itemSeparatorComponent} />}
                            renderItem={_showReplies}
                            contentContainerStyle={styles.flatListContainer}
                            showsVerticalScrollIndicator={false}
                            keyExtractor={item => item.Id.toString()}
                            ListHeaderComponent={() => {
                                return (
                                    <View style={styles.flatListHeaderContainer}>
                                        <View style={styles.commentInfoContainer}>
                                            <Image source={{ uri: commentData?.UserImageUrl ? GetImagePath(commentData?.UserImageUrl, profileImageSize) : 'data:image/png;base64,' }} resizeMode='contain'
                                                style={styles.personImage}
                                            />
                                            <View style={styles.nameContainer}>
                                                <Text style={styles.personNameText}>{commentData?.UserName ?? 'Anonymous'}</Text>
                                                {commentData?.Comment && <Text style={styles.commemtText}>{commentData?.Comment}</Text>}
                                                {commentData?.CommentImages && <Image source={{ uri: GetImagePath(commentData?.CommentImages, size) }} resizeMode='cover' style={styles.commentImage} />}
                                            </View>
                                        </View>
                                        <View style={styles.line} />
                                    </View>
                                )
                            }}
                        />
                    </View>
                    <View style={[styles.bottomContainer, newRepliesImage != '' ? { height: height(19) } : {}]}>
                        {newRepliesImage != '' &&
                            <ImageBackground source={{ uri: newRepliesImage }} resizeMode='cover' style={styles.newRepliesImage} >
                                <View style={styles.crossImageContainer}>
                                    <AntDesign name='closecircle' size={totalSize(2.5)} color={Colors.white}
                                        onPress={() => setNewRepliesImage('')}
                                    />
                                </View>
                            </ImageBackground>
                        }
                        <View style={styles.inputTextContainer}>
                            <FontAwesome name='camera' size={width(7)} color={Colors.appColor1} solid
                                onPress={uploadImage}
                            />
                            <View style={styles.textInputContainer}>
                                <TextInput
                                    placeholder={'Add a comment...'}
                                    placeholderTextColor={Colors.appTextColor6}
                                    autoCapitalize='none'
                                    style={styles.textInput}
                                    returnKeyType={'done'}
                                    value={newReplies}
                                    onChangeText={(val) => setNewCommentReplies(val)}
                                />
                            </View>
                            <TouchableOpacity style={styles.sentButton}
                                onPress={_postReplies}
                                disabled={((newReplies != '' || newRepliesImage != '') && !buttonDisable) ? false : true}
                            >
                                <Ionicons name='send' size={width(3)} color={Colors.white} />
                            </TouchableOpacity>
                        </View>
                    </View>
                </KeyboardAwareScrollView>
            </View>
            <SafeAreaView style={{ backgroundColor: Colors.white }} />
        </Fragment>
    );
}